package cn.bj.brook.algorithm.slidingwindow;

import org.junit.Assert;
import org.junit.Test;

public class TestMinScopeIncludingAllLetter {
    private MinScopeIncludingAllLetter func;

    public TestMinScopeIncludingAllLetter(){
        func = new MinScopeIncludingAllLetter();
    }

    @Test
    public void test1(){
        String rs = func.minWindow("ADOBECODEBANC", "ABC");
        Assert.assertEquals("BANC", rs);
    }

    @Test
    public void test2(){
        String rs = func.minWindow("GOOD BYE", "ED");
        Assert.assertEquals("D BYE", rs);
    }

    @Test
    public void test3(){
        String rs = func.minWindow("GOOD BYE", "BOY");
        Assert.assertEquals("OD BY", rs);
    }

    @Test
    public void test4(){
        String rs = func.minWindow("aa", "a");
        Assert.assertEquals("a", rs);
    }

    @Test
    public void test5(){
        String rs = func.minWindow("a", "a");
        Assert.assertEquals("a", rs);
    }

    @Test
    public void test6(){
        String source = "";
        String target = "";
        String rs = func.minWindow(source, target);
        System.out.println("rs="+rs);
        //Assert.assertEquals("a", rs);
    }

}
